"use strict";(self.webpackChunkspanish_app=self.webpackChunkspanish_app||[]).push([[304],{6304:e=>{e.exports=JSON.parse('{"13":{"words":[{"spanish":"Comprar","english":"To buy"},{"spanish":"Pagar","english":"To pay"},{"spanish":"El dinero","english":"The money"},{"spanish":"La tienda","english":"The store"},{"spanish":"El supermercado","english":"The supermarket"},{"spanish":"El mercado","english":"The market"},{"spanish":"Ir de compras","english":"To go shopping"},{"spanish":"Yo voy de compras","english":"I go shopping"},{"spanish":"Necesitar","english":"To need"},{"spanish":"Necesito comprar","english":"I need to buy"},{"spanish":"El precio","english":"The price"},{"spanish":"\xbfCu\xe1nto cuesta?","english":"How much does it cost?"},{"spanish":"Caro(a)","english":"Expensive"},{"spanish":"Barato(a)","english":"Cheap"},{"spanish":"La oferta","english":"The sale (offer)"},{"spanish":"La tarjeta de cr\xe9dito","english":"The credit card"},{"spanish":"El efectivo","english":"The cash"},{"spanish":"El recibo","english":"The receipt"},{"spanish":"Yo pago","english":"I pay"},{"spanish":"Yo pagu\xe9","english":"I paid"},{"spanish":"Yo voy a pagar","english":"I am going to pay"},{"spanish":"Yo compro","english":"I buy"},{"spanish":"Yo compr\xe9","english":"I bought"},{"spanish":"Yo voy a comprar","english":"I am going to buy"},{"spanish":"Ahorrar","english":"To save (money)"},{"spanish":"La lista de compras","english":"The shopping list"},{"spanish":"La caja","english":"The checkout"},{"spanish":"El cliente","english":"The customer"},{"spanish":"El(la) vendedor(a)","english":"The seller"}],"conversations":[{"id":1,"topic":"En la tienda","dialog":[{"speaker":"Cliente","spanish":"Disculpe, \xbfcu\xe1nto cuesta este vestido?","english":"Excuse me, how much does this dress cost?"},{"speaker":"Vendedora","spanish":"Cuesta cincuenta d\xf3lares. Es un poco caro, pero es muy bonito.","english":"It costs fifty dollars. It\'s a little expensive, but it\'s very pretty."},{"speaker":"Cliente","spanish":"Es verdad. \xbfTiene una oferta?","english":"It\'s true. Do you have a sale?"},{"speaker":"Vendedora","spanish":"S\xed, lo tenemos en oferta por cuarenta d\xf3lares.","english":"Yes, we have it on sale for forty dollars."},{"speaker":"Cliente","spanish":"\xa1Perfecto! Lo voy a comprar.","english":"Perfect! I am going to buy it."}],"questions":[{"question":"\xbfQu\xe9 est\xe1 comprando el cliente?","options":["Una camisa","Un vestido","Unos zapatos","Una falda"],"answer":"Un vestido","answerIndex":1},{"question":"\xbfCu\xe1nto cuesta el vestido con la oferta?","options":["Cincuenta d\xf3lares","Cuarenta d\xf3lares","Veinte d\xf3lares","Treinta d\xf3lares"],"answer":"Cuarenta d\xf3lares","answerIndex":1},{"question":"\xbfQu\xe9 significa \'Lo voy a comprar\'?","options":["I bought it","I am going to buy it","I want to buy it","I need to buy it"],"answer":"I am going to buy it","answerIndex":1}]},{"id":2,"topic":"En el supermercado","dialog":[{"speaker":"Laura","spanish":"Necesito ir al supermercado. Mi refrigerador est\xe1 vac\xedo.","english":"I need to go to the supermarket. My refrigerator is empty."},{"speaker":"Carlos","spanish":"Yo tambi\xe9n. Necesito comprar leche y pan.","english":"Me too. I need to buy milk and bread."},{"speaker":"Laura","spanish":"Yo compr\xe9 frutas y verduras ayer, as\xed que hoy solo necesito carne.","english":"I bought fruits and vegetables yesterday, so today I only need meat."},{"speaker":"Carlos","spanish":"Vamos juntos. Yo pago con tarjeta de cr\xe9dito, \xbfy t\xfa?","english":"Let\'s go together. I\'ll pay with a credit card, and you?"},{"speaker":"Laura","spanish":"Yo siempre pago con efectivo.","english":"I always pay with cash."}],"questions":[{"question":"\xbfQu\xe9 compr\xf3 Laura ayer?","options":["Leche y pan","Carne","Frutas y verduras","Nada"],"answer":"Frutas y verduras","answerIndex":2},{"question":"\xbfQu\xe9 necesita comprar Carlos?","options":["Leche y pan","Frutas y verduras","Carne","Un refrigerador"],"answer":"Leche y pan","answerIndex":0},{"question":"\xbfC\xf3mo paga Laura?","options":["Con tarjeta de cr\xe9dito","Con efectivo","No paga","Paga con la tarjeta de Carlos"],"answer":"Con efectivo","answerIndex":1}]},{"id":3,"topic":"Dinero y precios","dialog":[{"speaker":"Marcos","spanish":"Necesito ahorrar dinero para comprar una bicicleta nueva.","english":"I need to save money to buy a new bike."},{"speaker":"Silvia","spanish":"\xa1Qu\xe9 buena idea! Yo compr\xe9 una bicicleta el mes pasado.","english":"What a good idea! I bought a bicycle last month."},{"speaker":"Marcos","spanish":"\xbfFue muy cara?","english":"Was it very expensive?"},{"speaker":"Silvia","spanish":"No, no fue cara. La compr\xe9 en oferta. Cost\xf3 solo cien d\xf3lares.","english":"No, it wasn\'t expensive. I bought it on sale. It cost only one hundred dollars."},{"speaker":"Marcos","spanish":"\xa1Qu\xe9 suerte! Voy a buscar una oferta.","english":"How lucky! I\'m going to look for a sale."}],"questions":[{"question":"\xbfQu\xe9 quiere comprar Marcos?","options":["Una bicicleta nueva","Un coche","Un regalo","Unos zapatos"],"answer":"Una bicicleta nueva","answerIndex":0},{"question":"\xbfC\xf3mo compr\xf3 Silvia su bicicleta?","options":["La compr\xf3 muy cara","La compr\xf3 en oferta","La compr\xf3 de segunda mano","No compr\xf3 una bicicleta"],"answer":"La compr\xf3 en oferta","answerIndex":1},{"question":"\xbfQu\xe9 significa \'Fue muy cara\'?","options":["It was very cheap","It was very pretty","It was very fast","It was very expensive"],"answer":"It was very expensive","answerIndex":3}]},{"id":4,"topic":"Lista de compras","dialog":[{"speaker":"Julia","spanish":"Tengo que ir a la tienda a comprar ropa para el trabajo.","english":"I have to go to the store to buy clothes for work."},{"speaker":"David","spanish":"Yo tambi\xe9n. Necesito un pantal\xf3n nuevo y una camisa.","english":"Me too. I need new pants and a shirt."},{"speaker":"Julia","spanish":"Yo hice mi lista de compras. Voy a comprar un vestido y un abrigo.","english":"I made my shopping list. I am going to buy a dress and a coat."},{"speaker":"David","spanish":"\xbfVas a pagar con tarjeta?","english":"Are you going to pay with a card?"},{"speaker":"Julia","spanish":"S\xed, voy a pagar con la tarjeta de cr\xe9dito.","english":"Yes, I am going to pay with the credit card."}],"questions":[{"question":"\xbfPor qu\xe9 va Julia a la tienda?","options":["Para comprar comida","Para comprar ropa","Para comprar un regalo","Para comprar un coche"],"answer":"Para comprar ropa","answerIndex":1},{"question":"\xbfQu\xe9 compr\xf3 David?","options":["Un pantal\xf3n y una camisa","Un vestido y un abrigo","Nada","No se menciona"],"answer":"No se menciona","answerIndex":3},{"question":"\xbfC\xf3mo va a pagar Julia?","options":["Con efectivo","Con una tarjeta de cr\xe9dito","Con un cheque","Con el dinero de David"],"answer":"Con una tarjeta de cr\xe9dito","answerIndex":1}]},{"id":5,"topic":"Hablando del precio","dialog":[{"speaker":"Mar\xeda","spanish":"Mira qu\xe9 bonitos zapatos. \xbfCu\xe1nto costaron?","english":"Look at these pretty shoes. How much did they cost?"},{"speaker":"Pablo","spanish":"Costaron ochenta d\xf3lares. No fueron baratos, pero los necesitaba.","english":"They cost eighty dollars. They weren\'t cheap, but I needed them."},{"speaker":"Mar\xeda","spanish":"Entiendo. \xbfPagaste con tarjeta?","english":"I understand. Did you pay with a card?"},{"speaker":"Pablo","spanish":"S\xed, pagu\xe9 con tarjeta de cr\xe9dito. La tienda me dio el recibo.","english":"Yes, I paid with a credit card. The store gave me the receipt."},{"speaker":"Mar\xeda","spanish":"Te quedaron muy bien. Vali\xf3 la pena el precio.","english":"They look great on you. They were worth the price."}],"questions":[{"question":"\xbfCu\xe1nto costaron los zapatos?","options":["Diez d\xf3lares","Treinta d\xf3lares","Cincuenta d\xf3lares","Ochenta d\xf3lares"],"answer":"Ochenta d\xf3lares","answerIndex":3},{"question":"\xbfC\xf3mo pag\xf3 Pablo por los zapatos?","options":["Con efectivo","Con una tarjeta de cr\xe9dito","No pag\xf3","Con un cheque"],"answer":"Con una tarjeta de cr\xe9dito","answerIndex":1},{"question":"\xbfQu\xe9 significa \'Te quedaron muy bien\'?","options":["They were too small","They were a good price","They look great on you","They were a good color"],"answer":"They look great on you","answerIndex":2}]}],"stories":[{"id":1,"title":"Un D\xeda de Compras","sentences":[{"spanish":"El s\xe1bado pasado, mi amiga Ana y yo fuimos de compras. Fuimos a una tienda de ropa.","english":"Last Saturday, my friend Ana and I went shopping. We went to a clothing store."},{"spanish":"Yo necesitaba un pantal\xf3n y Ana quer\xeda un vestido. La tienda ten\xeda una oferta.","english":"I needed a pair of pants and Ana wanted a dress. The store had a sale."},{"spanish":"Encontr\xe9 un pantal\xf3n muy bonito y Ana encontr\xf3 un vestido. Ambos no fueron caros.","english":"I found a very nice pair of pants and Ana found a dress. Both were not expensive."},{"spanish":"Fuimos a la caja para pagar. Yo pagu\xe9 con mi tarjeta de cr\xe9dito y Ana pag\xf3 con efectivo.","english":"We went to the checkout to pay. I paid with my credit card and Ana paid with cash."},{"spanish":"Despu\xe9s, fuimos a una cafeter\xeda. Fue un d\xeda de compras divertido.","english":"Afterwards, we went to a cafe. It was a fun shopping day."}]},{"id":2,"title":"Ahorrando Dinero","sentences":[{"spanish":"Quiero comprar un nuevo televisor. Pero los televisores son muy caros.","english":"I want to buy a new television. But televisions are very expensive."},{"spanish":"Necesito ahorrar dinero. Por eso, no voy de compras cada semana.","english":"I need to save money. Because of this, I don\'t go shopping every week."},{"spanish":"El mes pasado, no compr\xe9 nada de ropa. Pagu\xe9 todas mis facturas con efectivo.","english":"Last month, I didn\'t buy any clothes. I paid all my bills with cash."},{"spanish":"Voy a comprar el televisor cuando tenga suficiente dinero.","english":"I am going to buy the television when I have enough money."},{"spanish":"Espero encontrar una oferta. Eso me ayudar\xeda mucho.","english":"I hope to find a sale. That would help me a lot."}]},{"id":3,"title":"El Mercado del Pueblo","sentences":[{"spanish":"Todos los domingos voy al mercado del pueblo con mi abuela. Ella ama el mercado.","english":"Every Sunday I go to the town market with my grandmother. She loves the market."},{"spanish":"En el mercado, hay muchas tiendas peque\xf1as y los precios son baratos.","english":"In the market, there are many small stores and the prices are cheap."},{"spanish":"La semana pasada, mi abuela compr\xf3 frutas, verduras y pescado. Le cost\xf3 poco dinero.","english":"Last week, my grandmother bought fruits, vegetables, and fish. It cost her little money."},{"spanish":"Mi abuela siempre paga con efectivo. Ella dice que el efectivo es m\xe1s simple.","english":"My grandmother always pays with cash. She says that cash is simpler."},{"spanish":"Yo le ayud\xe9 a llevar las bolsas a casa. Fue un d\xeda muy agradable.","english":"I helped her carry the bags home. It was a very pleasant day."}]}],"speaking":{"title":"De Compras y Dinero","description":"Preguntas y respuestas para practicar conversaciones sobre compras, precios y dinero.","questions_and_answers":[{"question":{"spanish":"\xbfQu\xe9 necesitas comprar hoy?","english":"What do you need to buy today?"},"answer":{"spanish":"Necesito comprar comida para la cena.","english":"I need to buy food for dinner."}},{"question":{"spanish":"\xbfD\xf3nde vas de compras?","english":"Where do you go shopping?"},"answer":{"spanish":"Yo voy al supermercado y al mercado.","english":"I go to the supermarket and the market."}},{"question":{"spanish":"\xbfCu\xe1nto cuesta esta camisa?","english":"How much does this shirt cost?"},"answer":{"spanish":"Cuesta veinte d\xf3lares.","english":"It costs twenty dollars."}},{"question":{"spanish":"\xbfC\xf3mo vas a pagar?","english":"How are you going to pay?"},"answer":{"spanish":"Voy a pagar con tarjeta de cr\xe9dito.","english":"I am going to pay with a credit card."}},{"question":{"spanish":"\xbfQu\xe9 compraste el s\xe1bado pasado?","english":"What did you buy last Saturday?"},"answer":{"spanish":"Compr\xe9 unos zapatos nuevos.","english":"I bought some new shoes."}},{"question":{"spanish":"\xbfEs caro el precio?","english":"Is the price expensive?"},"answer":{"spanish":"No, no es caro. Es un buen precio.","english":"No, it\'s not expensive. It\'s a good price."}},{"question":{"spanish":"\xbfPrefieres pagar con efectivo o con tarjeta?","english":"Do you prefer to pay with cash or with a card?"},"answer":{"spanish":"Prefiero pagar con efectivo.","english":"I prefer to pay with cash."}},{"question":{"spanish":"\xbfNecesitas ahorrar dinero?","english":"Do you need to save money?"},"answer":{"spanish":"S\xed, necesito ahorrar para un viaje.","english":"Yes, I need to save for a trip."}},{"question":{"spanish":"\xbfHay una oferta en la tienda?","english":"Is there a sale at the store?"},"answer":{"spanish":"S\xed, hay una oferta en todos los pantalones.","english":"Yes, there is a sale on all pants."}},{"question":{"spanish":"\xbfQu\xe9 compraste en el mercado?","english":"What did you buy at the market?"},"answer":{"spanish":"Compr\xe9 frutas y verduras frescas.","english":"I bought fresh fruits and vegetables."}},{"question":{"spanish":"\xbfEl recibo es importante?","english":"Is the receipt important?"},"answer":{"spanish":"S\xed, el recibo es importante por si acaso.","english":"Yes, the receipt is important just in case."}},{"question":{"spanish":"\xbfQui\xe9n es el vendedor?","english":"Who is the seller?"},"answer":{"spanish":"El vendedor es la persona que trabaja en la tienda.","english":"The seller is the person who works in the store."}},{"question":{"spanish":"\xbfTe gusta ir de compras?","english":"Do you like to go shopping?"},"answer":{"spanish":"No, no me gusta ir de compras. Me aburro.","english":"No, I don\'t like to go shopping. I get bored."}},{"question":{"spanish":"\xbfD\xf3nde est\xe1 la caja?","english":"Where is the checkout?"},"answer":{"spanish":"La caja est\xe1 al final de la tienda.","english":"The checkout is at the end of the store."}},{"question":{"spanish":"\xbfQu\xe9 vas a comprar en la tienda de ropa?","english":"What are you going to buy at the clothing store?"},"answer":{"spanish":"Voy a comprar un vestido y un abrigo.","english":"I am going to buy a dress and a coat."}},{"question":{"spanish":"\xbfQu\xe9 pagaste el mes pasado?","english":"What did you pay for last month?"},"answer":{"spanish":"Pagu\xe9 por la comida y la ropa.","english":"I paid for food and clothes."}},{"question":{"spanish":"\xbfT\xfa tienes una lista de compras?","english":"Do you have a shopping list?"},"answer":{"spanish":"S\xed, siempre hago una lista de compras.","english":"Yes, I always make a shopping list."}},{"question":{"spanish":"\xbfEs esta tienda cara o barata?","english":"Is this store expensive or cheap?"},"answer":{"spanish":"Esta tienda es un poco cara.","english":"This store is a little expensive."}},{"question":{"spanish":"\xbfQu\xe9 necesita el cliente?","english":"What does the customer need?"},"answer":{"spanish":"El cliente necesita ayuda para encontrar un producto.","english":"The customer needs help to find a product."}},{"question":{"spanish":"\xbfVas a ahorrar dinero para algo?","english":"Are you going to save money for something?"},"answer":{"spanish":"S\xed, voy a ahorrar para comprar una computadora nueva.","english":"Yes, I am going to save to buy a new computer."}}]}}}')}}]);